import axios from 'axios';
import './App.css';
import React, { useEffect, useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css'

function App() {

  const [todoList, setTodoList] = useState([{}]);
  const [title, setTitle] = useState('');
  const [desc, setDesc] = useState('');

  useEffect(() => {
    axios.get('http:localhost:8000/api/todo').then(res => {
      setTodoList(res.data)
    })
  });

  return (
    <div className="App">
      <div className='App list-group-item justify-content-center align-items-center mx-auto' style={{ width: "400px", backgroundColor: "white", marginTop: "15px" }}>
        <h1 className='card text-white bg-primary mb-1'>Task Manager</h1>
        <h6 className='card text-white bg-primary mb-3'>FastAPI - React - MongoDB</h6>
        <div className='card-body'>
          <h5 className='card text-white bg-dark mb-3'> Add Your Tasks</h5>
          <span className='card-text'>
            <input className='mb-2 form-control titleIn' placeholder='Title' />
            <input className='mb-2 form-control desIn' placeholder='Description' />
            <button className='btn btn-outline-primary mx-3 mb-3' style={{ borderRadius:"50px", fontWeight:"Bold" }}>Add Task</button>
          </span>

          <h5 className='card text-white bg-dark mb-3'>Your Tasks</h5>
          <div>

          </div>
        </div>
        <h6 className='card text-dark bg-warning py-1 mb-0'>Copyright 2024, All rights received &copy;</h6>
      </div>
    </div>
  );
}

export default App;
