import axios from 'axios';
import './App.css';
import React, { useEffect, useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import TodoView from './components/TodoListView';

function App() {

  const [todoList, setTodoList] = useState([]);
  const [title, setTitle] = useState('');
  const [desc, setDesc] = useState('');
  const [darkMode, setDarkMode] = useState(false);

  useEffect(() => {
    axios.get('http://localhost:8000/api/todo').then(res => {
      setTodoList(res.data);
    });
  }, []);

  const addTodoItem = () => {
    axios.post('http://localhost:8000/api/todo/', { 'title': title, 'description': desc })
    .then(res => {
      setTodoList([...todoList, res.data]);
      setTitle('');
      setDesc('');
    });
  };

  const toggleDarkMode = () => {
    setDarkMode(!darkMode);
  };

  return (
    <div className={`App ${darkMode ? 'dark-mode' : ''}`}>
      <div className='container'>
        <div className='App list-group-item justify-content-center align-items-center mx-auto' style={{ maxWidth: "500px", marginTop: "15px" }}>
          <h1 className='card text-white bg-primary mb-1'>Task Manager</h1>
          <h6 className='card text-white bg-primary mb-3'>FastAPI - React - MongoDB</h6>
          <div className='card-body'>
            <h5 className='card text-white bg-dark mb-3'> Add Your Tasks</h5>
            <span className='card-text'>
              <input className='mb-2 form-control titleIn' placeholder='Title' value={title} onChange={event=>setTitle(event.target.value)} />
              <input className='mb-2 form-control desIn' placeholder='Description' value={desc} onChange={event=>setDesc(event.target.value)} />
              <button className='btn btn-outline-primary mb-3' style={{ borderRadius: "50px", fontWeight: "Bold" }} onClick={addTodoItem}>Add Task</button>
            </span>

            <h5 className='card text-white bg-dark mb-3'>Your Tasks</h5>
            <div>
              <TodoView todoList={todoList} />
            </div>
          </div>
          <h6 className='card text-dark bg-warning py-1 mb-0'>Copyright 2024, All rights reserved &copy;</h6>
          <button className='btn btn-secondary mt-3' onClick={toggleDarkMode}>
            {darkMode ? 'Switch to Light Mode' : 'Switch to Dark Mode'}
          </button>
        </div>
      </div>
    </div>
  );
}

export default App;
